mapper.select:
select
from gen_history t
and t.deleted=0 and t.history_id = #{arg0}

select count(1) from gen_history

    and deleted=0
    and history_id = #{arg0}

mapper.delete:
update gen_history set deleted=1
where history_id=#{arg0,jdbcType=INTEGER}
and deleted=0

mapper.select:
select * from gen_history t
and t.deleted=0
order by t.created_time desc limit #{startIndex},#{pageSize}
